import React, { Component } from "react";
import axios from "axios";
import CarCard from "./CarCard";
import { connect } from "react-redux";

class CarInventory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cars: []
    };
  }

  componentDidMount() {
    this.getCars();
  }

  getCars = () => {
    axios("http://localhost:5000")
      .then(data => {
        console.log(data.data.cars);
        this.setState({ cars: data.data.cars });
      })
      // .then(({response}) => this.setState({users: response.users}))
      .catch(error => console.log(error));
  };

  showCars() {
    return this.state.cars.map((car, index) => {
      return <CarCard key={index} car={car} />;
    });
  }
  render() {
    return (
      <div className="carInventory">
        <div className="car-container">{this.showCars()}</div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    cars: state.cars
  };
}

export default CarInventory;
