import React, { Component } from "react";
import CarCard from "./CarCard";

class CarInventory extends Component {
  showCars() {
    return this.props.cars.map((car, index) => {
      return <CarCard key={index} car={car} />;
    });
  }

  componentWillMount() {
    this.props.dispatch(actions.fetchCars());
  }

  render() {
    return (
      <div className="carInventory">
        <div className="car-container">{this.showCars()}</div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    cars: state.cars.data
  };
}

export default connect(mapStateToProps)(CarInventory);
