import React, { Component } from "react";
import logo from "./logo.svg";
import axios from "axios";

import "./scss/styles.scss";
import {
  BrowserRouter as Router,
  Route,
  Link,
  Redirect
} from "react-router-dom";

import * as redux from "redux";
import { Provider } from "react-redux";

import Main from "./Components/Main";
import Header from "./Components/Header";
import CarInventory from "./Components/CarInventory";
import CarDetail from "./Components/CarDetail";

class App extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     cars: []
  //   };
  // }
  // componentDidMount() {
  //   this.getCars();
  // }

  // getCars = () => {
  //   axios("http://localhost:5000")
  //     .then(data => {
  //       console.log(data.data.cars);
  //       this.setState({ cars: data.data.cars });
  //     })
  //     // .then(({response}) => this.setState({users: response.users}))
  //     .catch(error => console.log(error));
  // };
  render() {
    const store = redux.createStore(() => {
      return {
        cars: [
          {
            id: 1,
            title: "Kia",
            image: "kia.png",
            body: "4-door sedan",
            year: 2015,
            model: "Optima",
            mileage: "23,000",
            price: "18,000",
            created_at: "11-12-2019",
            updated_at: "0"
          },
          {
            id: 4,
            title: "Mercedes",
            image: "mercedes.png",
            body: "4-door sedan",
            year: 2009,
            model: "S-Class",
            mileage: "34,000",
            price: "30,000",
            created_at: "11-08-2019",
            updated_at: "0"
          },
          {
            id: 5,
            title: "Audi",
            image: "audi.png",
            body: "4-door sedan",
            year: 2017,
            model: "A3",
            mileage: "13,000",
            price: "44,000",
            created_at: "11-08-2019",
            updated_at: "0"
          },
          {
            id: 6,
            title: "Hummer",
            image: "hummer.png",
            body: "4-door",
            year: 2013,
            model: "H2",
            mileage: "21,000",
            price: "45,000",
            created_at: "11-08-2019",
            updated_at: "0"
          },
          {
            id: 7,
            title: "BMW",
            image: "bmw.png",
            body: "4-door",
            year: 2017,
            model: "320i",
            mileage: "9,000",
            price: "38,000",
            created_at: "11-08-2019",
            updated_at: "0"
          },
          {
            id: 8,
            title: "Lamborghini",
            image: "lambo.png",
            body: "2-door",
            year: 2017,
            model: "Aventador",
            mileage: "8,000",
            price: "400,000",
            created_at: "11-08-2019",
            updated_at: "0"
          }
        ]
      };
    });
    return (
      <Provider store={store}>
        <Router>
          <div className="app">
            <Header />
            <Route
              exact
              path="/"
              render={() => {
                return <Redirect to="/mainPage" />;
              }}
            />
            <Route exact path="/mainPage" component={Main} />
            <Route exact path="/carInventory" component={CarInventory} />
            <Route exact path="/carInventory/:id" component={CarDetail} />
          </div>
        </Router>
      </Provider>
    );
  }
}

export default App;
